import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';  // Import Socket.io client

// Connect to the backend server
const socket = io('http://localhost:5000');  // Make sure to use the correct port

function App() {
  // State to manage messages and input field
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  // On component mount, listen for incoming messages from the server
    useEffect(() => {
        // Listen for incoming messages from the server
        socket.on('message', (msg) => {
            setMessages((prevMessages) => [...prevMessages, msg]);  // Add the new message to the chat
        });

        // Clean up the listener when the component unmounts
        return () => {
            socket.off('message');  // This removes the listener when the component unmounts
        };
    }, []);  // The empty dependency array ensures this effect runs only once when the component is mounted


    // Function to handle sending messages
  const sendMessage = () => {
    if (input) {
      socket.emit('message', input);  // Send the input message to the server
      setInput('');  // Clear the input field
    }
  };



  return (
      <div style={{ padding: '20px' }}>
        <h1>Real-Time Chatbot</h1>

        {/* Chat messages display */}
        <div style={{ border: '1px solid #ddd', padding: '10px', height: '300px', overflowY: 'scroll' }}>
          {messages.map((msg, index) => (
              <p key={index}>{msg}</p>
          ))}
        </div>

        {/* Input field for sending messages */}
        <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
            style={{ marginTop: '10px', width: '80%' }}
        />

        {/* Button to send messages */}
        <button onClick={sendMessage} style={{ marginLeft: '10px' }}>
          Send
        </button>
      </div>
  );
}

export default App;
